mapper.select:
  select 

  from base_operation_log


	 		       FUID  = #{fuid}  


	 		   and    USERID  = #{userid}  


	 		   and    USERNAME  = #{username}  


	 		   and    REALNAME  = #{realname}  


	 		   and    DESCRIPTION  = #{description}  


	 		   and    OPERATING  = #{operating}  


	 		   and    CREATEDATE  = #{createdate}

mapper.delete:

  DELETE FROM base_operation_log WHERE 

	 		       FUID  = #{fuid}  


	 		   and    USERID  = #{userid}  


	 		   and    USERNAME  = #{username}  


	 		   and    REALNAME  = #{realname}  


	 		   and    DESCRIPTION  = #{description}  


	 		   and    OPERATING  = #{operating}  


	 		   and    CREATEDATE  = #{createdate}
