mapper.select:
  select 

  from base_datadictionary


	 		       FUID  = #{fuid}  


	 		   and    FULLNAME  = #{fullname}  


	 		   and    CODE  = #{code}  


	 		   and    SEQUENCE  = #{sequence}  


	 		   and    DESCRIPTION  = #{description}  


	 		   and    CREATEDATE  = #{createdate}  


	 		   and    MODIFYDATE  = #{modifydate}  


	 		   and    MODIFYUSERREALNAME  = #{modifyuserrealname}

